import { useEffect, useState } from "react";
import Card from "../UI/Card";
import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItem/MealItem";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);

  // useEffect(() => {
  //   const fetchMeals = async () => {
  //     const response = await fetch("https://react-customhooks-b138b-default-rtdb.firebaseio.com/meals.json");
  //     const responseData = await response.json();

  //     const loadedMeals = [];

  //     for (const key in responseData) {
  //       loadedMeals.push({
  //         id: key,
  //         name: responseData[key].name,
  //         description: responseData[key].description,
  //         price: responseData[key].price,
  //       });
  //     }
  //     setMeals(loadedMeals);
  //   };
  //   fetchMeals();
  // }, []);
  fetch(
    "https://react-customhooks-b138b-default-rtdb.firebaseio.com/meals.json"
  )
    .then((response) => {
      if (!response.ok) {
        throw new Error("ERROR in database");
      }
      return response.json();
    })
    .then((data) => {
      const loadedMeals = [];

            loadedMeals.push({
              id: data.key,
              name: data.name,
              description: data.description,
              price: data.price,
            });
          
     return loadedMeals
    }).then(loadedMeals => setMeals(loadedMeals))
    .catch((error) => {
      console.log("Fetch error: ", error);
    });
  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
